      script.on_nth_tick(1, function()
        local finished = game.move_cursor{position = target}
        player.raw_build_from_cursor{created_by_moving = true}
        if finished then
          step_4()
        end
      end)
    end

    step_4 = function()
      local target = player.position
      script.on_nth_tick(1, function()
        local finished = game.move_cursor{position = target}
        if finished then
          reset()
        end
      end)
    end

    reset = function()
